use starterm_workflows_types::*;
pub fn workflow() -> Workflow {
    Workflow {name: r####"Clone git repository with specific SSH Key and User"####.into(),command: r####"git -c core.sshCommand='ssh -i {{sshKeyPath}} -o IdentitiesOnly=yes' clone {{repositoryUrl}} {{targetFolder}}
cd {{targetFolder}}
git config core.sshCommand 'ssh -i {{sshKeyPath}}'
git config user.name "{{userName}}"
git config user.email {{userEmail}}"####.into(),tags: vec![r####"git"####.into(),r####"ssh"####.into()].into_iter().collect(),description: Some(r####"Clones a git repository given a specific SSH Key Path and configures it to use the desired Name and Email"####.into()),arguments: vec![Argument {name: r####"sshKeyPath"####.into(),description: Some(r####"The path of the SSH Key to be used"####.into()),default_value: Some(r####"~/.ssh/id_rsa"####.into()),},Argument {name: r####"repositoryUrl"####.into(),description: Some(r####"The SSH URL of the git repository"####.into()),default_value: Some(r####"<repo_url>"####.into()),},Argument {name: r####"targetFolder"####.into(),description: Some(r####"The name of the folder in which the repository should be cloned into"####.into()),default_value: Some(r####"<target_folder>"####.into()),},Argument {name: r####"userName"####.into(),description: Some(r####"The Name of the User to be configured for the git repository"####.into()),default_value: Some(r####"Jhon Doe"####.into()),},Argument {name: r####"userEmail"####.into(),description: Some(r####"The Email of the User to be configured for the git repository"####.into()),default_value: Some(r####"johndoe@example.com"####.into()),}].into_iter().collect(),source_url: Some(r####"https://github.com/charlieVader/warp-workflows/blob/master/git/clone-with-ssh.yaml"####.into()),author: Some(r####"charlieVader"####.into()),author_url: Some(r####"https://github.com/charlieVader"####.into()),shells: vec![].into_iter().collect(),}
}
